import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';

const API_BASE = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000/api';

// Contract addresses (update after deployment)
const ART_TOKEN_ADDRESS = process.env.REACT_APP_ART_TOKEN_ADDRESS;
const AUCTION_ADDRESS = process.env.REACT_APP_AUCTION_ADDRESS;

// Simple ABI for demonstration
const ART_TOKEN_ABI = [
  "function safeMint(address to) public returns (uint256)",
  "function ownerOf(uint256 tokenId) public view returns (address)",
  "function transferFrom(address from, address to, uint256 tokenId) public",
  "event Transfer(address indexed from, address indexed to, uint256 indexed tokenId)"
];

const AUCTION_ABI = [
  "function createAuction(uint256 _tokenId, uint256 _startPrice, uint256 _durationInMinutes) external",
  "function placeBid(uint256 _auctionId) external payable",
  "function endAuction(uint256 _auctionId) external",
  "function getAuctionDetails(uint256 _auctionId) external view returns (tuple(uint256 tokenId, address seller, uint256 startTime, uint256 endTime, uint256 startPrice, address highestBidder, uint256 highestBid, bool ended))",
  "event AuctionCreated(uint256 indexed auctionId, uint256 indexed tokenId, address seller, uint256 startPrice, uint256 endTime)",
  "event NewBid(uint256 indexed auctionId, address bidder, uint256 amount)",
  "event AuctionEnded(uint256 indexed auctionId, address winner, uint256 amount)"
];

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState('');
  const [allAccounts, setAllAccounts] = useState([]);
  const [artTokenContract, setArtTokenContract] = useState(null);
  const [auctionContract, setAuctionContract] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState('gallery');
  const [isAutoConnecting, setIsAutoConnecting] = useState(true);

  // Form states
  const [nftForm, setNftForm] = useState({
    name: '',
    description: '',
    image: null
  });
  const [auctionForm, setAuctionForm] = useState({
    tokenId: '',
    startPrice: '',
    duration: ''
  });
  const [bidForm, setBidForm] = useState({
    auctionId: '',
    amount: ''
  });

  // Auto-connect on component mount
  useEffect(() => {
    checkWalletConnection();
  }, []);

  useEffect(() => {
    if (account) {
      fetchNFTs();
      fetchActiveAuctions();
      initializeContracts();
    }
  }, [account, signer]);

  // Listen for account changes
  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', handleChainChanged);
      
      return () => {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum.removeListener('chainChanged', handleChainChanged);
      };
    }
  }, []);

  const handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      // User disconnected wallet
      setAccount('');
      setAllAccounts([]);
      setSigner(null);
      setProvider(null);
      setArtTokenContract(null);
      setAuctionContract(null);
      showMessage('Wallet disconnected', 'error');
    } else if (accounts[0] !== account) {
      // User switched accounts
      setAccount(accounts[0]);
      showMessage(`Switched to account: ${accounts[0].slice(0, 6)}...${accounts[0].slice(-4)}`, 'success');
      initializeContractsWithAccount(accounts[0]);
    }
  };

  const handleChainChanged = (chainId) => {
    // Reload the page when network changes
    window.location.reload();
  };

  const checkWalletConnection = async () => {
    setIsAutoConnecting(true);
    try {
      if (!window.ethereum) {
        setIsAutoConnecting(false);
        return;
      }

      // Check if we're already connected
      const accounts = await window.ethereum.request({ 
        method: 'eth_accounts' 
      });

      if (accounts.length > 0) {
        // Auto-connect to the first authorized account
        await initializeWallet(accounts[0]);
        showMessage('Auto-connected to wallet', 'success');
      }
    } catch (error) {
      console.error('Auto-connect error:', error);
    } finally {
      setIsAutoConnecting(false);
    }
  };

  const initializeWallet = async (accountAddress = null) => {
    try {
      if (!window.ethereum) {
        throw new Error('Please install MetaMask!');
      }

      const web3Provider = new ethers.BrowserProvider(window.ethereum);
      setProvider(web3Provider);
      
      let web3Signer;
      if (accountAddress) {
        // Use specific account
        web3Signer = await web3Provider.getSigner(accountAddress);
      } else {
        // Request account access
        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });
        web3Signer = await web3Provider.getSigner(accounts[0]);
      }
      
      setSigner(web3Signer);
      
      const userAddress = await web3Signer.getAddress();
      setAccount(userAddress);

      // Get all available accounts
      const allAccounts = await window.ethereum.request({
        method: 'eth_accounts'
      });
      setAllAccounts(allAccounts);

      await initializeContracts(web3Signer);

      showMessage('Wallet connected successfully!', 'success');
    } catch (error) {
      console.error('Wallet initialization error:', error);
      throw error;
    }
  };

  const initializeContracts = async (specificSigner = null) => {
    try {
      const signerToUse = specificSigner || signer;
      if (!signerToUse) return;

      const artToken = new ethers.Contract(ART_TOKEN_ADDRESS, ART_TOKEN_ABI, signerToUse);
      const auction = new ethers.Contract(AUCTION_ADDRESS, AUCTION_ABI, signerToUse);
      
      setArtTokenContract(artToken);
      setAuctionContract(auction);
    } catch (error) {
      console.error('Contract initialization error:', error);
    }
  };

  const initializeContractsWithAccount = async (accountAddress) => {
    if (!provider) return;
    
    try {
      const web3Signer = await provider.getSigner(accountAddress);
      await initializeContracts(web3Signer);
    } catch (error) {
      console.error('Error initializing contracts with account:', error);
    }
  };

  const connectWallet = async () => {
    setLoading(true);
    setError('');
    try {
      await initializeWallet();
    } catch (error) {
      console.error('Wallet connection error:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const switchAccount = async (accountAddress) => {
    setLoading(true);
    try {
      await initializeWallet(accountAddress);
      showMessage(`Switched to account: ${accountAddress.slice(0, 6)}...${accountAddress.slice(-4)}`, 'success');
    } catch (error) {
      console.error('Account switch error:', error);
      showMessage('Failed to switch account');
    } finally {
      setLoading(false);
    }
  };

  const disconnectWallet = () => {
    setAccount('');
    setAllAccounts([]);
    setSigner(null);
    setProvider(null);
    setArtTokenContract(null);
    setAuctionContract(null);
    setNfts([]);
    setAuctions([]);
    showMessage('Wallet disconnected', 'success');
  };

  const showMessage = (message, type = 'error') => {
    if (type === 'error') {
      setError(message);
      setTimeout(() => setError(''), 5000);
    } else {
      setSuccess(message);
      setTimeout(() => setSuccess(''), 5000);
    }
  };

  const fetchNFTs = async () => {
    try {
      const response = await fetch(`${API_BASE}/nfts`);
      if (!response.ok) throw new Error('Failed to fetch NFTs');
      const data = await response.json();
      setNfts(data);
    } catch (error) {
      console.error('Error fetching NFTs:', error);
      showMessage('Failed to load NFTs');
    }
  };

  const fetchActiveAuctions = async () => {
    try {
      const response = await fetch(`${API_BASE}/active-auctions`);
      if (!response.ok) throw new Error('Failed to fetch auctions');
      const data = await response.json();
      setAuctions(data);
    } catch (error) {
      console.error('Error fetching auctions:', error);
      showMessage('Failed to load auctions');
    }
  };

  const createNFT = async (e) => {
    e.preventDefault();
    if (!nftForm.name || !nftForm.image) {
      showMessage('Please fill all required fields');
      return;
    }

    setLoading(true);
    try {
      // Upload artwork to backend
      const formData = new FormData();
      formData.append('name', nftForm.name);
      formData.append('description', nftForm.description);
      formData.append('artist', 'Digital Artist');
      formData.append('artistAddress', account);
      formData.append('image', nftForm.image);

      const uploadResponse = await fetch(`${API_BASE}/upload-artwork`, {
        method: 'POST',
        body: formData
      });

      const uploadData = await uploadResponse.json();
      
      if (!uploadResponse.ok) {
        throw new Error(uploadData.error || 'Upload failed');
      }

      // Mint NFT on blockchain
      const tx = await artTokenContract.safeMint(account);
      const receipt = await tx.wait();

      // Get token ID from event
      const transferEvent = receipt.logs.find(
        log => log.fragment && log.fragment.name === 'Transfer'
      );
      const tokenId = transferEvent ? transferEvent.args[2].toString() : '0';

      // Save metadata to backend
      const saveResponse = await fetch(`${API_BASE}/nft-metadata`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          tokenId,
          ...uploadData.metadata,
          imageIpfsHash: uploadData.imageIpfsHash,
          metadataIpfsHash: uploadData.metadataIpfsHash
        })
      });

      if (!saveResponse.ok) {
        throw new Error('Failed to save metadata');
      }

      showMessage('NFT created successfully!', 'success');
      setNftForm({ name: '', description: '', image: null });
      fetchNFTs();
    } catch (error) {
      console.error('Error creating NFT:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const createAuction = async (e) => {
    e.preventDefault();
    if (!auctionForm.tokenId || !auctionForm.startPrice || !auctionForm.duration) {
      showMessage('Please fill all required fields');
      return;
    }

    setLoading(true);
    try {
      const tx = await auctionContract.createAuction(
        auctionForm.tokenId,
        ethers.parseEther(auctionForm.startPrice),
        auctionForm.duration
      );
      await tx.wait();
      
      showMessage('Auction created successfully!', 'success');
      setAuctionForm({ tokenId: '', startPrice: '', duration: '' });
      fetchActiveAuctions();
    } catch (error) {
      console.error('Error creating auction:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const placeBid = async (e) => {
    e.preventDefault();
    if (!bidForm.auctionId || !bidForm.amount) {
      showMessage('Please fill all required fields');
      return;
    }

    setLoading(true);
    try {
      const tx = await auctionContract.placeBid(bidForm.auctionId, {
        value: ethers.parseEther(bidForm.amount)
      });
      await tx.wait();
      
      showMessage('Bid placed successfully!', 'success');
      setBidForm({ auctionId: '', amount: '' });
      fetchActiveAuctions();
    } catch (error) {
      console.error('Error placing bid:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const formatTimeRemaining = (endTime) => {
    const now = new Date();
    const end = new Date(endTime);
    const diff = end - now;
    
    if (diff <= 0) return 'Ended';
    
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    
    return `${hours}h ${minutes}m`;
  };

  const formatAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1> Digital Art Auction Platform</h1>
        {!account ? (
          <div className="wallet-actions">
            <button 
              onClick={connectWallet} 
              disabled={loading || isAutoConnecting}
              className="connect-btn"
            >
              {isAutoConnecting ? 'Checking...' : loading ? 'Connecting...' : 'Connect MetaMask'}
            </button>
          </div>
        ) : (
          <div className="user-info">
            <div className="account-selector">
              <span className="current-account">
                Connected: {formatAddress(account)}
              </span>
              {allAccounts.length > 1 && (
                <div className="accounts-dropdown">
                  <select 
                    value={account}
                    onChange={(e) => switchAccount(e.target.value)}
                    className="account-select"
                  >
                    {allAccounts.map((acc) => (
                      <option key={acc} value={acc}>
                        {formatAddress(acc)} {acc === account ? '(Current)' : ''}
                      </option>
                    ))}
                  </select>
                </div>
              )}
              <button 
                onClick={disconnectWallet}
                className="disconnect-btn"
              >
                Disconnect
              </button>
            </div>
          </div>
        )}
      </header>

      {/* Messages */}
      {error && <div className="message error">{error}</div>}
      {success && <div className="message success">{success}</div>}

      {account && (
        <div className="main-content">
          {/* Navigation Tabs */}
          <nav className="tabs">
            <button 
              className={activeTab === 'gallery' ? 'active' : ''}
              onClick={() => setActiveTab('gallery')}
            >
              Art Gallery
            </button>
            <button 
              className={activeTab === 'create' ? 'active' : ''}
              onClick={() => setActiveTab('create')}
            >
              Create NFT
            </button>
            <button 
              className={activeTab === 'auctions' ? 'active' : ''}
              onClick={() => setActiveTab('auctions')}
            >
              Active Auctions
            </button>
            <button 
              className={activeTab === 'bid' ? 'active' : ''}
              onClick={() => setActiveTab('bid')}
            >
              Place Bid
            </button>
          </nav>

          {/* Create NFT Section */}
          {activeTab === 'create' && (
            <section className="section">
              <h2>Create New Artwork</h2>
              <form onSubmit={createNFT} className="form">
                <input
                  type="text"
                  placeholder="Artwork Name *"
                  value={nftForm.name}
                  onChange={(e) => setNftForm({...nftForm, name: e.target.value})}
                  required
                />
                <textarea
                  placeholder="Description"
                  value={nftForm.description}
                  onChange={(e) => setNftForm({...nftForm, description: e.target.value})}
                  rows="3"
                />
                <div className="file-upload">
                  <label>Artwork Image *</label>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setNftForm({...nftForm, image: e.target.files[0]})}
                    required
                  />
                </div>
                <button type="submit" disabled={loading} className="submit-btn">
                  {loading ? 'Creating NFT...' : 'Create NFT'}
                </button>
              </form>
            </section>
          )}

          {/* Create Auction Section */}
          {activeTab === 'create' && (
            <section className="section">
              <h2>Create Auction</h2>
              <form onSubmit={createAuction} className="form">
                <input
                  type="number"
                  placeholder="Token ID *"
                  value={auctionForm.tokenId}
                  onChange={(e) => setAuctionForm({...auctionForm, tokenId: e.target.value})}
                  required
                />
                <input
                  type="text"
                  placeholder="Start Price (ETH) *"
                  value={auctionForm.startPrice}
                  onChange={(e) => setAuctionForm({...auctionForm, startPrice: e.target.value})}
                  required
                />
                <input
                  type="number"
                  placeholder="Duration (minutes) *"
                  value={auctionForm.duration}
                  onChange={(e) => setAuctionForm({...auctionForm, duration: e.target.value})}
                  required
                />
                <button type="submit" disabled={loading} className="submit-btn">
                  {loading ? 'Creating Auction...' : 'Create Auction'}
                </button>
              </form>
            </section>
          )}

          {/* Place Bid Section */}
          {activeTab === 'bid' && (
            <section className="section">
              <h2>Place Bid</h2>
              <form onSubmit={placeBid} className="form">
                <input
                  type="number"
                  placeholder="Auction ID *"
                  value={bidForm.auctionId}
                  onChange={(e) => setBidForm({...bidForm, auctionId: e.target.value})}
                  required
                />
                <input
                  type="text"
                  placeholder="Bid Amount (ETH) *"
                  value={bidForm.amount}
                  onChange={(e) => setBidForm({...bidForm, amount: e.target.value})}
                  required
                />
                <button type="submit" disabled={loading} className="submit-btn">
                  {loading ? 'Placing Bid...' : 'Place Bid'}
                </button>
              </form>
            </section>
          )}

          {/* NFTs Gallery */}
          {activeTab === 'gallery' && (
            <section className="section">
              <h2>Artwork Gallery ({nfts.length} items)</h2>
              {nfts.length === 0 ? (
                <div className="empty-state">
                  <p>No artworks yet. Create your first NFT!</p>
                </div>
              ) : (
                <div className="nft-grid">
                  {nfts.map(nft => (
                    <div key={nft._id} className="nft-card">
                      <div className="nft-image">
                        <img 
                          src={nft.image} 
                          alt={nft.name}
                          onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/300x300?text=Artwork';
                          }}
                        />
                      </div>
                      <div className="nft-info">
                        <h3>{nft.name}</h3>
                        <p className="description">{nft.description}</p>
                        <div className="nft-meta">
                          <small>Token ID: {nft.tokenId}</small>
                          <small>Artist: {formatAddress(nft.artistAddress)}</small>
                          <small>Created: {new Date(nft.createdAt).toLocaleDateString()}</small>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </section>
          )}

          {/* Active Auctions */}
          {activeTab === 'auctions' && (
            <section className="section">
              <h2>Active Auctions ({auctions.length})</h2>
              {auctions.length === 0 ? (
                <div className="empty-state">
                  <p>No active auctions. Create one from your NFTs!</p>
                </div>
              ) : (
                <div className="auctions-grid">
                  {auctions.map(auction => (
                    <div key={auction._id} className="auction-card">
                      <div className="auction-header">
                        <h3>Auction #{auction.auctionId}</h3>
                        <span className={`status ${formatTimeRemaining(auction.endTime) === 'Ended' ? 'ended' : 'active'}`}>
                          {formatTimeRemaining(auction.endTime)}
                        </span>
                      </div>
                      
                      {auction.nftMetadata && (
                        <div className="auction-nft">
                          <img 
                            src={auction.nftMetadata.image} 
                            alt={auction.nftMetadata.name}
                            onError={(e) => {
                              e.target.src = 'https://via.placeholder.com/200x200?text=Artwork';
                            }}
                          />
                          <div className="nft-details">
                            <h4>{auction.nftMetadata.name}</h4>
                            <p>{auction.nftMetadata.description}</p>
                          </div>
                        </div>
                      )}
                      
                      <div className="auction-details">
                        <div className="detail-row">
                          <span>Token ID:</span>
                          <span>{auction.tokenId}</span>
                        </div>
                        <div className="detail-row">
                          <span>Seller:</span>
                          <span className="address">{formatAddress(auction.seller)}</span>
                        </div>
                        <div className="detail-row">
                          <span>Current Bid:</span>
                          <span className="bid-amount">
                            {ethers.formatEther(auction.highestBid || '0')} ETH
                          </span>
                        </div>
                        <div className="detail-row">
                          <span>Bids:</span>
                          <span>{auction.bids?.length || 0}</span>
                        </div>
                        <div className="detail-row">
                          <span>Ends:</span>
                          <span>{new Date(auction.endTime).toLocaleString()}</span>
                        </div>
                      </div>
                      
                      {auction.highestBidder && (
                        <div className="current-bidder">
                          Highest bidder: {formatAddress(auction.highestBidder)}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </section>
          )}
        </div>
      )}

      {loading && (
        <div className="loading-overlay">
          <div className="loading-spinner">Loading...</div>
        </div>
      )}
    </div>
  );
}

export default App;
