import React, { useState, useEffect, useRef } from 'react';
import { ethers } from 'ethers';
import './App.css';

// Contract addresses (update after deployment)
const ART_TOKEN_ADDRESS = process.env.REACT_APP_ART_TOKEN_ADDRESS;
const AUCTION_ADDRESS = process.env.REACT_APP_AUCTION_ADDRESS;
const API_BASE = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000/api';

// Simple ABI for demonstration (use full ABI from compilation)
const ART_TOKEN_ABI = [
  "function safeMint(address to) public returns (uint256)",
  "function ownerOf(uint256 tokenId) public view returns (address)",
  "function transferFrom(address from, address to, uint256 tokenId) public",
  "function approve(address to, uint256 tokenId) public",
  "function balanceOf(address owner) public view returns (uint256)",
  "function currentTokenId() public view returns (uint256)",
  "event Transfer(address indexed from, address indexed to, uint256 indexed tokenId)"
];

const AUCTION_ABI = [
  "function createAuction(uint256 _tokenId, uint256 _startPrice, uint256 _durationInMinutes) external",
  "function placeBid(uint256 _auctionId) external payable",
  "function endAuction(uint256 _auctionId) external",
  "function getAuctionDetails(uint256 _auctionId) external view returns (tuple(uint256 tokenId, address seller, uint256 startTime, uint256 endTime, uint256 startPrice, address highestBidder, uint256 highestBid, bool ended))",
  "function auctions(uint256) public view returns (uint256 tokenId, address seller, uint256 startTime, uint256 endTime, uint256 startPrice, address highestBidder, uint256 highestBid, bool ended)",
  "function getCurrentAuctionId() public view returns (uint256)",
  "event AuctionCreated(uint256 indexed auctionId, uint256 indexed tokenId, address seller, uint256 startPrice, uint256 endTime)",
  "event NewBid(uint256 indexed auctionId, address bidder, uint256 amount)",
  "event AuctionEnded(uint256 indexed auctionId, address winner, uint256 amount)"
];

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState('');
  const [artTokenContract, setArtTokenContract] = useState(null);
  const [auctionContract, setAuctionContract] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState('gallery');

  // Form states
  const [nftForm, setNftForm] = useState({
    name: '',
    description: '',
    image: null
  });
  const [auctionForm, setAuctionForm] = useState({
    tokenId: '',
    startPrice: '',
    duration: ''
  });
  const [bidForm, setBidForm] = useState({
    auctionId: '',
    amount: ''
  });

  // Refs Ä‘á»ƒ trÃ¡nh re-render vÃ´ háº¡n vÃ  theo dÃµi state hiá»‡n táº¡i
  const isInitialized = useRef(false);
  const currentAccountRef = useRef('');

  // HÃ m khá»Ÿi táº¡o contracts
  const initializeContracts = async (web3Signer) => {
    try {
      const artToken = new ethers.Contract(ART_TOKEN_ADDRESS, ART_TOKEN_ABI, web3Signer);
      const auction = new ethers.Contract(AUCTION_ADDRESS, AUCTION_ABI, web3Signer);
      
      setArtTokenContract(artToken);
      setAuctionContract(auction);
      console.log('âœ… Contracts initialized for account:', await web3Signer.getAddress());
    } catch (error) {
      console.error('âŒ Error initializing contracts:', error);
    }
  };

  // HÃ m cáº­p nháº­t provider vÃ  signer
  const updateProviderAndSigner = async () => {
    if (window.ethereum) {
      try {
        const web3Provider = new ethers.BrowserProvider(window.ethereum);
        const newSigner = await web3Provider.getSigner();
        const newAccount = await newSigner.getAddress();
        
        setProvider(web3Provider);
        setSigner(newSigner);
        setAccount(newAccount);
        currentAccountRef.current = newAccount; // Cáº­p nháº­t ref
        
        await initializeContracts(newSigner);
        
        console.log('ğŸ”„ Account updated to:', newAccount);
        showMessage(`ÄÃ£ chuyá»ƒn sang tÃ i khoáº£n: ${newAccount.slice(0, 6)}...${newAccount.slice(-4)}`, 'success');
        
        // Fetch láº¡i dá»¯ liá»‡u
        fetchNFTs();
        fetchActiveAuctions();
      } catch (error) {
        console.error('âŒ Error updating provider and signer:', error);
      }
    }
  };

  // HÃ m xá»­ lÃ½ sá»± kiá»‡n accountsChanged - ÄÃƒ Sá»¬A
  const handleAccountsChanged = async (accounts) => {
    console.log('ğŸ”„ Accounts changed:', accounts);
    console.log('ğŸ“ Current account in ref:', currentAccountRef.current);
    
    if (accounts.length === 0) {
      // NgÆ°á»i dÃ¹ng Ä‘Ã£ disconnect
      console.log('ğŸ‘¤ User disconnected');
      setAccount('');
      currentAccountRef.current = '';
      setProvider(null);
      setSigner(null);
      setArtTokenContract(null);
      setAuctionContract(null);
      showMessage('ÄÃ£ ngáº¯t káº¿t ná»‘i vÃ­', 'error');
    } else {
      // NgÆ°á»i dÃ¹ng Ä‘Ã£ chuyá»ƒn tÃ i khoáº£n
      const newAccount = accounts[0];
      console.log('ğŸ” Comparing:', newAccount.toLowerCase(), 'vs', currentAccountRef.current.toLowerCase());
      
      // So sÃ¡nh vá»›i ref thay vÃ¬ state
      if (newAccount.toLowerCase() !== currentAccountRef.current.toLowerCase()) {
        console.log('ğŸ”„ Switching to new account:', newAccount);
        await updateProviderAndSigner();
      } else {
        console.log('âœ… Same account, no change needed');
      }
    }
  };

  // HÃ m xá»­ lÃ½ sá»± kiá»‡n chainChanged
  const handleChainChanged = (chainId) => {
    console.log('ğŸ”„ Chain changed:', chainId);
    // Reload page khi chain thay Ä‘á»•i
    window.location.reload();
  };

  useEffect(() => {
    if (account && artTokenContract && auctionContract) {
      fetchNFTs();
      fetchActiveAuctions();
    }
  }, [account, artTokenContract, auctionContract]);

  useEffect(() => {
    // Chá»‰ thiáº¿t láº­p event listeners má»™t láº§n
    if (isInitialized.current) return;
    
    if (window.ethereum) {
      console.log('ğŸ”§ Setting up MetaMask event listeners...');
      
      // Kiá»ƒm tra xem Ä‘Ã£ cÃ³ account nÃ o Ä‘Æ°á»£c káº¿t ná»‘i chÆ°a
      const checkConnectedAccount = async () => {
        try {
          const accounts = await window.ethereum.request({ 
            method: 'eth_accounts' 
          });
          if (accounts.length > 0) {
            console.log('ğŸ” Found connected account:', accounts[0]);
            // Tá»± Ä‘á»™ng káº¿t ná»‘i vá»›i account Ä‘Ã£ Ä‘Æ°á»£c káº¿t ná»‘i trÆ°á»›c Ä‘Ã³
            await updateProviderAndSigner();
          }
        } catch (error) {
          console.error('âŒ Error checking connected accounts:', error);
        }
      };
      
      checkConnectedAccount();
      
      // Thiáº¿t láº­p event listeners
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', handleChainChanged);
      
      isInitialized.current = true;

      // Cleanup function
      return () => {
        console.log('ğŸ§¹ Cleaning up event listeners...');
        if (window.ethereum) {
          window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
