      });

      if (accounts.length === 0) {
        throw new Error('KhÃ´ng cÃ³ tÃ i khoáº£n nÃ o Ä‘Æ°á»£c káº¿t ná»‘i');
      }

      const web3Provider = new ethers.BrowserProvider(window.ethereum);
      const web3Signer = await web3Provider.getSigner();
      const userAddress = await web3Signer.getAddress();

      setProvider(web3Provider);
      setSigner(web3Signer);
      setAccount(userAddress);
      currentAccountRef.current = userAddress; // Cáº­p nháº­t ref

      // Khá»Ÿi táº¡o contracts
      await initializeContracts(web3Signer);

      showMessage(`Káº¿t ná»‘i vÃ­ thÃ nh cÃ´ng! TÃ i khoáº£n: ${userAddress.slice(0, 6)}...${userAddress.slice(-4)}`, 'success');
      
    } catch (error) {
      console.error('âŒ Lá»—i káº¿t ná»‘i vÃ­:', error);
      
      let errorMessage = error.message;
      if (error.code === 4001) {
        errorMessage = 'NgÆ°á»i dÃ¹ng Ä‘Ã£ tá»« chá»‘i káº¿t ná»‘i vÃ­';
