    formData.append('name', nftForm.name);
    formData.append('description', nftForm.description || '');
    formData.append('artist', 'Digital Artist');
    formData.append('artistAddress', account);
    formData.append('image', nftForm.image);

    console.log('ğŸ“¤ Uploading to backend...');
    const uploadResponse = await fetch(`${API_BASE}/upload-artwork`, {
      method: 'POST',
      body: formData
    });

    if (!uploadResponse.ok) {
      const errorText = await uploadResponse.text();
      throw new Error(`Upload failed: ${errorText}`);
    }

    const uploadData = await uploadResponse.json();
    console.log('âœ… Upload successful:', uploadData);

    // Mint NFT on blockchain - THÃŠM ERROR HANDLING
    console.log('ğŸª™ Minting NFT on blockchain...');
    
    // Kiá»ƒm tra contract
    if (!artTokenContract) {
      throw new Error('ArtToken contract not initialized');
    }

    // Test call trÆ°á»›c
    try {
      const testCall = await artTokenContract.owner();
      console.log('âœ… Contract test call successful');
    } catch (testError) {
      console.error('âŒ Contract test failed:', testError);
      throw new Error('Blockchain connection failed: ' + testError.message);
    }

    // Thá»±c hiá»‡n mint vá»›i timeout
    const tx = await Promise.race([
      artTokenContract.safeMint(account),
      new Promise((_, reject) => 
        setTimeout(() => reject(new Error('Transaction timeout')), 30000)
      )
    ]);
    
    console.log('â³ Waiting for transaction confirmation...');
    const receipt = await tx.wait();
    console.log('âœ… Transaction confirmed:', receipt);

    // Láº¥y token ID tá»« event
    const transferEvent = receipt.logs.find(
      log => log.fragment && log.fragment.name === 'Transfer'
    );
    
    const tokenId = transferEvent ? transferEvent.args[2].toString() : '1';
    console.log('ğŸ¨ NFT minted with token ID:', tokenId);

    // Save metadata to backend
    console.log('ğŸ’¾ Saving metadata...');
    const saveResponse = await fetch(`${API_BASE}/nft-metadata`, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
