        metadataIpfsHash: uploadData.metadataIpfsHash
      })
    });

    if (!saveResponse.ok) {
      throw new Error('Failed to save metadata');
    }

    showMessage('NFT created successfully!', 'success');
    setNftForm({ name: '', description: '', image: null });
    fetchNFTs();
    
  } catch (error) {
    console.error('âŒ Error creating NFT:', error);
    showMessage(`Creation failed: ${error.message}`);
  } finally {
