import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';

// Contract addresses (update after deployment)
const ART_TOKEN_ADDRESS = process.env.REACT_APP_ART_TOKEN_ADDRESS;
const AUCTION_ADDRESS = process.env.REACT_APP_AUCTION_ADDRESS;
const API_BASE = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000/api';

// Simple ABI for demonstration (use full ABI from compilation)
const ART_TOKEN_ABI = [
  "function safeMint(address to) public returns (uint256)",
  "function ownerOf(uint256 tokenId) public view returns (address)",
  "function transferFrom(address from, address to, uint256 tokenId) public",
  "function approve(address to, uint256 tokenId) public",
  "function balanceOf(address owner) public view returns (uint256)",
  "function currentTokenId() public view returns (uint256)",
  "function getApproved(uint256 tokenId) public view returns (address)",
  "event Transfer(address indexed from, address indexed to, uint256 indexed tokenId)"
];

const AUCTION_ABI = [
  "function createAuction(uint256 _tokenId, uint256 _startPrice, uint256 _durationInMinutes) external",
  "function placeBid(uint256 _auctionId) external payable",
  "function endAuction(uint256 _auctionId) external",
  "function getAuctionDetails(uint256 _auctionId) external view returns (tuple(uint256 tokenId, address seller, uint256 startTime, uint256 endTime, uint256 startPrice, address highestBidder, uint256 highestBid, bool ended))",
  "function auctions(uint256) public view returns (uint256 tokenId, address seller, uint256 startTime, uint256 endTime, uint256 startPrice, address highestBidder, uint256 highestBid, bool ended)",
  "function getCurrentAuctionId() public view returns (uint256)",
  "event AuctionCreated(uint256 indexed auctionId, uint256 indexed tokenId, address seller, uint256 startPrice, uint256 endTime)",
  "event NewBid(uint256 indexed auctionId, address bidder, uint256 amount)",
  "event AuctionEnded(uint256 indexed auctionId, address winner, uint256 amount)"
];

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState('');
  const [artTokenContract, setArtTokenContract] = useState(null);
  const [auctionContract, setAuctionContract] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState('gallery');

  // Form states
  const [nftForm, setNftForm] = useState({
    name: '',
    description: '',
    image: null
  });
  const [auctionForm, setAuctionForm] = useState({
    tokenId: '',
    startPrice: '',
    duration: ''
  });
  const [bidForm, setBidForm] = useState({
    auctionId: '',
    amount: ''
  });

  useEffect(() => {
    if (account) {
      fetchNFTs();
      fetchActiveAuctions();
    }
  }, [account]);

  useEffect(() => {
    // L·∫Øng nghe s·ª± ki·ªán thay ƒë·ªïi account trong MetaMask
    if (window.ethereum) {
      const handleAccountsChanged = (accounts) => {
        if (accounts.length > 0) {
          setAccount(accounts[0]);
          // C·∫≠p nh·∫≠t l·∫°i signer v√† contracts
          updateProviderAndSigner();
        } else {
          // Ng∆∞·ªùi d√πng ƒë√£ disconnect
          setAccount('');
          setProvider(null);
          setSigner(null);
        }
      };

      const handleChainChanged = () => {
        // Reload khi chain thay ƒë·ªïi
        window.location.reload();
      };

      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', handleChainChanged);

      // Cleanup
      return () => {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum.removeListener('chainChanged', handleChainChanged);
      };
    }
  }, []);

  const updateProviderAndSigner = async () => {
    if (window.ethereum && account) {
      try {
        const web3Provider = new ethers.BrowserProvider(window.ethereum);
        const newSigner = await web3Provider.getSigner();
        setSigner(newSigner);
        
        const artToken = new ethers.Contract(ART_TOKEN_ADDRESS, ART_TOKEN_ABI, newSigner);
        const auction = new ethers.Contract(AUCTION_ADDRESS, AUCTION_ABI, newSigner);
        
        setArtTokenContract(artToken);
        setAuctionContract(auction);
      } catch (error) {
        console.error('L·ªói c·∫≠p nh·∫≠t provider:', error);
      }
    }
  };

  const switchAccount = async () => {
    try {
      setLoading(true);
      // Y√™u c·∫ßu MetaMask cho ph√©p ch·ªçn account m·ªõi
      await window.ethereum.request({
        method: 'wallet_requestAccounts'
      });
      // Event listener s·∫Ω t·ª± ƒë·ªông x·ª≠ l√Ω ph·∫ßn c√≤n l·∫°i
    } catch (error) {
      console.error('L·ªói khi chuy·ªÉn t√†i kho·∫£n:', error);
      if (error.code === 4001) {
        showMessage('Ng∆∞·ªùi d√πng ƒë√£ t·ª´ ch·ªëi chuy·ªÉn t√†i kho·∫£n');
      } else {
        showMessage('L·ªói khi chuy·ªÉn t√†i kho·∫£n');
      }
    } finally {
      setLoading(false);
    }
  };

  const disconnectWallet = () => {
    setAccount('');
    setProvider(null);
    setSigner(null);
    setArtTokenContract(null);
    setAuctionContract(null);
    showMessage('ƒê√£ ng·∫Øt k·∫øt n·ªëi v√≠', 'success');
  };

  const showMessage = (message, type = 'error') => {
    if (type === 'error') {
      setError(message);
      setTimeout(() => setError(''), 5000);
    } else {
      setSuccess(message);
      setTimeout(() => setSuccess(''), 5000);
    }
  };

  const connectWallet = async () => {
    setLoading(true);
    setError('');
    try {
      if (!window.ethereum) {
        throw new Error('Vui l√≤ng t·∫£i MetaMask!');
      }

      await window.ethereum.request({ method: 'eth_requestAccounts' });
      const web3Provider = new ethers.BrowserProvider(window.ethereum);
      setProvider(web3Provider);
      
      const web3Signer = await web3Provider.getSigner();
      setSigner(web3Signer);
      
      const userAddress = await web3Signer.getAddress();
      setAccount(userAddress);

      // Initialize contracts
      const artToken = new ethers.Contract(ART_TOKEN_ADDRESS, ART_TOKEN_ABI, web3Signer);
      const auction = new ethers.Contract(AUCTION_ADDRESS, AUCTION_ABI, web3Signer);
      
      setArtTokenContract(artToken);
      setAuctionContract(auction);

      showMessage('K·∫øt n·ªëi v√≠ th√†nh c√¥ng!', 'success');
    } catch (error) {
      console.error('L·ªói k·∫øt n·ªëi v√≠:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchNFTs = async () => {
    try {
      const response = await fetch(`${API_BASE}/nfts`);
      if (!response.ok) throw new Error('Kh√¥ng th·ªÉ l·∫•y NFTs');
      const data = await response.json();
      setNfts(data);
    } catch (error) {
      console.error('L·ªói t·∫£i NFTs:', error);
      showMessage('Kh√¥ng th·ªÉ t·∫£i NFTs');
    }
  };

  const fetchActiveAuctions = async () => {
    try {
      const response = await fetch(`${API_BASE}/active-auctions`);
      if (!response.ok) throw new Error('Kh√¥ng th·ªÉ l·∫•y ƒë·∫•u gi√°');
      const data = await response.json();
      setAuctions(data);
    } catch (error) {
      console.error('L·ªói t·∫£i ƒë·∫•u gi√°:', error);
      showMessage('Kh√¥ng th·ªÉ t·∫£i ƒë·∫•u gi√°');
    }
  };

  const createNFT = async (e) => {
    e.preventDefault();
    if (!nftForm.name || !nftForm.image) {
      showMessage('Vui l√≤ng ƒëi·ªÅn t·∫•t c·∫£ c√°c tr∆∞·ªùng b·∫Øt bu·ªôc');
      return;
    }

    setLoading(true);
    try {
      console.log('üîÑ B·∫Øt ƒë·∫ßu qu√° tr√¨nh t·∫°o NFT...');

      // Upload artwork to backend
      const formData = new FormData();
      formData.append('name', nftForm.name);
      formData.append('description', nftForm.description || '');
      formData.append('artist', 'Digital Artist');
      formData.append('artistAddress', account);
      formData.append('image', nftForm.image);

      console.log('üì§ ƒêang upload ·∫£nh...');
      const uploadResponse = await fetch(`${API_BASE}/upload-artwork`, {
        method: 'POST',
        body: formData
      });

      if (!uploadResponse.ok) {
        throw new Error(`Upload th·∫•t b·∫°i: ${uploadResponse.status}`);
      }

      const uploadData = await uploadResponse.json();
      console.log('‚úÖ Upload th√†nh c√¥ng:', uploadData);

      // Mint NFT on blockchain
      console.log('‚õìÔ∏è ƒêang mint NFT tr√™n blockchain...');
      const tx = await artTokenContract.safeMint(account);
      console.log('üì® Transaction sent:', tx.hash);
      
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed');

      // Get token ID t·ª´ event
      let tokenId = '1'; // M·∫∑c ƒë·ªãnh
      try {
        const currentId = await artTokenContract.currentTokenId();
        tokenId = currentId.toString();
        console.log('üéØ Token ID:', tokenId);
      } catch (error) {
        console.log('Kh√¥ng l·∫•y ƒë∆∞·ª£c token ID, s·ª≠ d·ª•ng m·∫∑c ƒë·ªãnh');
      }

      // Save metadata to backend
      console.log('üíæ ƒêang l∆∞u metadata...');
      const saveResponse = await fetch(`${API_BASE}/nft-metadata`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          tokenId,
          name: nftForm.name,
          description: nftForm.description,
          image: uploadData.metadata?.image || `https://ipfs.io/ipfs/${uploadData.imageIpfsHash}`,
          imageIpfsHash: uploadData.imageIpfsHash,
          metadataIpfsHash: uploadData.metadataIpfsHash,
          artist: 'Digital Artist',
          artistAddress: account
        })
      });

      if (!saveResponse.ok) {
        throw new Error('L∆∞u metadata th·∫•t b·∫°i');
      }

      showMessage('T·∫°o NFT th√†nh c√¥ng!', 'success');
      setNftForm({ name: '', description: '', image: null });
      
      // Reset file input
      const fileInput = document.querySelector('input[type="file"]');
      if (fileInput) fileInput.value = '';
      
      fetchNFTs();
      
    } catch (error) {
      console.error('‚ùå L·ªói t·∫°o NFT:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const debugAuction = async () => {
    try {
      console.log('=== DEBUG AUCTION ===');
      console.log('Account:', account);
      console.log('Token ID:', auctionForm.tokenId);
      console.log('Start Price:', auctionForm.startPrice);
      console.log('Duration:', auctionForm.duration);
      
      if (artTokenContract && auctionForm.tokenId) {
        try {
          const owner = await artTokenContract.ownerOf(auctionForm.tokenId);
          console.log('NFT Owner:', owner);
          console.log('Is owner?', owner.toLowerCase() === account.toLowerCase());
        } catch (e) {
          console.log('L·ªói ki·ªÉm tra ownership:', e.message);
        }
      }
      console.log('=== END DEBUG ===');
    } catch (error) {
      console.error('Debug error:', error);
    }
  };

  const createAuction = async (e) => {
    e.preventDefault();
    
    // Validate input
    if (!auctionForm.tokenId || !auctionForm.startPrice || !auctionForm.duration) {
      showMessage('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin');
      return;
    }

    const tokenId = parseInt(auctionForm.tokenId);
    const duration = parseInt(auctionForm.duration);
    const startPrice = parseFloat(auctionForm.startPrice);

    if (isNaN(tokenId) || tokenId <= 0) {
      showMessage('Token ID ph·∫£i l√† s·ªë l·ªõn h∆°n 0');
      return;
    }

    if (isNaN(duration) || duration <= 0) {
      showMessage('Th·ªùi gian ph·∫£i l√† s·ªë ph√∫t l·ªõn h∆°n 0');
      return;
    }

    if (isNaN(startPrice) || startPrice <= 0) {
      showMessage('Gi√° kh·ªüi ƒëi·ªÉm ph·∫£i l√† s·ªë l·ªõn h∆°n 0');
      return;
    }

    setLoading(true);
    try {
      // Debug tr∆∞·ªõc
      await debugAuction();

      // Ki·ªÉm tra ownership
      console.log('üîç Ki·ªÉm tra quy·ªÅn s·ªü h·ªØu NFT...');
      const owner = await artTokenContract.ownerOf(tokenId);
      if (owner.toLowerCase() !== account.toLowerCase()) {
        throw new Error('B·∫°n kh√¥ng s·ªü h·ªØu NFT n√†y. Token ID: ' + tokenId);
      }

      // Ki·ªÉm tra v√† approve n·∫øu c·∫ßn
      console.log('üîç Ki·ªÉm tra approval...');
      const approvedAddress = await artTokenContract.getApproved(tokenId);
      const auctionAddress = await auctionContract.getAddress();
      
      if (approvedAddress.toLowerCase() !== auctionAddress.toLowerCase()) {
        console.log('‚úÖ ƒêang approve NFT cho contract...');
        const approveTx = await artTokenContract.approve(auctionAddress, tokenId);
        await approveTx.wait();
        console.log('‚úÖ Approve th√†nh c√¥ng');
      }

      // T·∫°o auction
      console.log('üöÄ ƒêang t·∫°o auction...');
      const startPriceInWei = ethers.parseEther(auctionForm.startPrice);
      
      const tx = await auctionContract.createAuction(
        tokenId,
        startPriceInWei,
        duration
      );

      console.log('üì® Transaction sent:', tx.hash);
      showMessage('ƒêang t·∫°o phi√™n ƒë·∫•u gi√°...', 'success');

      const receipt = await tx.wait();
      console.log('‚úÖ Auction created:', receipt);

      showMessage('T·∫°o phi√™n ƒë·∫•u gi√° th√†nh c√¥ng!', 'success');
      setAuctionForm({ tokenId: '', startPrice: '', duration: '' });
      fetchActiveAuctions();

    } catch (error) {
      console.error('‚ùå L·ªói t·∫°o ƒë·∫•u gi√°:', error);
      
      let errorMessage = error.message;
      if (error.message.includes('user rejected')) {
        errorMessage = 'Ng∆∞·ªùi d√πng ƒë√£ h·ªßy giao d·ªãch';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Kh√¥ng ƒë·ªß ETH ƒë·ªÉ th·ª±c hi·ªán giao d·ªãch';
      } else if (error.message.includes('Not the owner')) {
        errorMessage = 'B·∫°n kh√¥ng s·ªü h·ªØu NFT n√†y';
      } else if (error.message.includes('nonexistent token')) {
        errorMessage = 'NFT kh√¥ng t·ªìn t·∫°i';
      }
      
      showMessage(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const placeBid = async (e) => {
    e.preventDefault();
    if (!bidForm.auctionId || !bidForm.amount) {
      showMessage('Vui l√≤ng ƒëi·ªÅn t·∫•t c·∫£ c√°c tr∆∞·ªùng b·∫Øt bu·ªôc');
      return;
    }

    const auctionId = parseInt(bidForm.auctionId);
    const amount = parseFloat(bidForm.amount);

    if (isNaN(auctionId) || auctionId <= 0) {
      showMessage('Auction ID ph·∫£i l√† s·ªë l·ªõn h∆°n 0');
      return;
    }

    if (isNaN(amount) || amount <= 0) {
      showMessage('S·ªë ti·ªÅn ƒë·∫∑t ph·∫£i l·ªõn h∆°n 0');
      return;
    }

    setLoading(true);
    try {
      const tx = await auctionContract.placeBid(auctionId, {
        value: ethers.parseEther(bidForm.amount)
      });
      
      console.log('üì® Bid transaction sent:', tx.hash);
      showMessage('ƒêang x·ª≠ l√Ω ƒë·∫∑t gi√°...', 'success');

      await tx.wait();
      
      showMessage('ƒê·∫∑t gi√° th√†nh c√¥ng!', 'success');
      setBidForm({ auctionId: '', amount: '' });
      fetchActiveAuctions();
    } catch (error) {
      console.error('L·ªói ƒë·∫∑t gi√°:', error);
      
      let errorMessage = error.message;
      if (error.message.includes('user rejected')) {
        errorMessage = 'Ng∆∞·ªùi d√πng ƒë√£ h·ªßy giao d·ªãch';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Kh√¥ng ƒë·ªß ETH ƒë·ªÉ ƒë·∫∑t gi√°';
      } else if (error.message.includes('Bid too low')) {
        errorMessage = 'Gi√° ƒë·∫∑t qu√° th·∫•p';
      } else if (error.message.includes('Auction not active')) {
        errorMessage = 'Phi√™n ƒë·∫•u gi√° kh√¥ng c√≤n ho·∫°t ƒë·ªông';
      }
      
      showMessage(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const formatTimeRemaining = (endTime) => {
    const now = new Date();
    const end = new Date(endTime);
    const diff = end - now;
    
    if (diff <= 0) return 'ƒê√£ k·∫øt th√∫c';
    
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    
    return `${hours}gi·ªù ${minutes}ph√∫t`;
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>üé® N·ªÅn t·∫£ng ƒë·∫•u gi√° ngh·ªá thu·∫≠t s·ªë</h1>
        {!account ? (
          <button 
            onClick={connectWallet} 
            disabled={loading}
            className="connect-btn"
          >
            {loading ? 'ƒêang k·∫øt n·ªëi...' : 'K·∫øt n·ªëi MetaMask'}
          </button>
        ) : (
          <div className="user-info">
            <div className="account-display">
              <span className="account-address">
                üë§ {account.slice(0, 6)}...{account.slice(-4)}
              </span>
              <div className="account-actions">
                <button onClick={switchAccount} className="switch-btn" disabled={loading}>
                  {loading ? 'ƒêang chuy·ªÉn...' : 'üîÑ ƒê·ªïi TK'}
                </button>
                <button onClick={disconnectWallet} className="disconnect-btn">
                  ‚ùå Ng·∫Øt k·∫øt n·ªëi
                </button>
              </div>
            </div>
          </div>
        )}
      </header>

      {/* Messages */}
      {error && <div className="message error">{error}</div>}
      {success && <div className="message success">{success}</div>}

      {account && (
        <div className="main-content">
          {/* Navigation Tabs */}
          <nav className="tabs">
            <button 
              className={activeTab === 'gallery' ? 'active' : ''}
              onClick={() => setActiveTab('gallery')}
            >
              Th∆∞ vi·ªán ngh·ªá thu·∫≠t
            </button>
            <button 
              className={activeTab === 'create' ? 'active' : ''}
              onClick={() => setActiveTab('create')}
            >
              T·∫°o NFT
            </button>
            <button 
              className={activeTab === 'auctions' ? 'active' : ''}
              onClick={() => setActiveTab('auctions')}
            >
              ƒê·∫•u gi√° ƒëang di·ªÖn ra
            </button>
            <button 
              className={activeTab === 'bid' ? 'active' : ''}
              onClick={() => setActiveTab('bid')}
            >
              Ra gi√°
            </button>
          </nav>

          {/* Create NFT Section */}
          {activeTab === 'create' && (
            <section className="section">
              <h2>T·∫°o t√°c ph·∫©m m·ªõi</h2>
              <form onSubmit={createNFT} className="form">
                <div className="form-group">
                  <label>T√™n t√°c ph·∫©m *</label>
                  <input
                    type="text"
                    placeholder="Nh·∫≠p t√™n t√°c ph·∫©m"
                    value={nftForm.name}
                    onChange={(e) => setNftForm({...nftForm, name: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>M√¥ t·∫£</label>
                  <textarea
                    placeholder="M√¥ t·∫£ v·ªÅ t√°c ph·∫©m"
                    value={nftForm.description}
                    onChange={(e) => setNftForm({...nftForm, description: e.target.value})}
                    rows="3"
                  />
                </div>
                <div className="form-group">
                  <label>·∫¢nh t√°c ph·∫©m *</label>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setNftForm({...nftForm, image: e.target.files[0]})}
                    required
                  />
                  <small>Ch·ªçn file ·∫£nh cho t√°c ph·∫©m c·ªßa b·∫°n</small>
                </div>
                <button type="submit" disabled={loading} className="submit-btn">
                  {loading ? 'ƒêang t·∫°o NFT...' : 'T·∫°o NFT'}
                </button>
              </form>
            </section>
          )}

          {/* Create Auction Section */}
          {activeTab === 'create' && (
            <section className="section">
              <h2>T·∫°o ƒë·∫•u gi√°</h2>
              <form onSubmit={createAuction} className="form">
                <div className="form-group">
                  <label>Token ID *</label>
                  <input
                    type="number"
                    placeholder="Nh·∫≠p Token ID c·ªßa NFT"
                    value={auctionForm.tokenId}
                    onChange={(e) => setAuctionForm({...auctionForm, tokenId: e.target.value})}
                    min="1"
                    required
                  />
                  <small>Token ID c·ªßa NFT b·∫°n mu·ªën ƒë·∫•u gi√°</small>
                </div>
                <div className="form-group">
                  <label>Gi√° kh·ªüi ƒëi·ªÉm (ETH) *</label>
                  <input
