import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';

// Contract addresses (update after deployment)
const ART_TOKEN_ADDRESS = process.env.REACT_APP_ART_TOKEN_ADDRESS;
const AUCTION_ADDRESS = process.env.REACT_APP_AUCTION_ADDRESS;
const API_BASE = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000/api';

// Simple ABI for demonstration (use full ABI from compilation)
const ART_TOKEN_ABI = [
  "function safeMint(address to) public returns (uint256)",
  "function ownerOf(uint256 tokenId) public view returns (address)",
  "function transferFrom(address from, address to, uint256 tokenId) public",
  "event Transfer(address indexed from, address indexed to, uint256 indexed tokenId)"
];

const AUCTION_ABI = [
  "function createAuction(uint256 _tokenId, uint256 _startPrice, uint256 _durationInMinutes) external",
  "function placeBid(uint256 _auctionId) external payable",
  "function endAuction(uint256 _auctionId) external",
  "function getAuctionDetails(uint256 _auctionId) external view returns (tuple(uint256 tokenId, address seller, uint256 startTime, uint256 endTime, uint256 startPrice, address highestBidder, uint256 highestBid, bool ended))",
  "event AuctionCreated(uint256 indexed auctionId, uint256 indexed tokenId, address seller, uint256 startPrice, uint256 endTime)",
  "event NewBid(uint256 indexed auctionId, address bidder, uint256 amount)",
  "event AuctionEnded(uint256 indexed auctionId, address winner, uint256 amount)"
];

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState('');
  const [artTokenContract, setArtTokenContract] = useState(null);
  const [auctionContract, setAuctionContract] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [activeTab, setActiveTab] = useState('gallery');

  // Form states
  const [nftForm, setNftForm] = useState({
    name: '',
    description: '',
    image: null
  });
  const [auctionForm, setAuctionForm] = useState({
    tokenId: '',
    startPrice: '',
    duration: ''
  });
  const [bidForm, setBidForm] = useState({
    auctionId: '',
    amount: ''
  });

  useEffect(() => {
    if (account) {
      fetchNFTs();
      fetchActiveAuctions();
    }
  }, [account]);

  const switchAccount = async () => {
    try {
      // YÃªu cáº§u MetaMask switch account
      await window.ethereum.request({
        method: 'wallet_requestPermissions',
        params: [{ eth_accounts: {} }]
      });
      
      // Reload Ä‘á»ƒ cáº­p nháº­t account
      window.location.reload();
    } catch (error) {
      console.error('Error switching account:', error);
    }
  };

  const showMessage = (message, type = 'error') => {
    if (type === 'error') {
      setError(message);
      setTimeout(() => setError(''), 5000);
    } else {
      setSuccess(message);
      setTimeout(() => setSuccess(''), 5000);
    }
  };

  const connectWallet = async () => {
    setLoading(true);
    setError('');
    try {
      if (!window.ethereum) {
        throw new Error('Vui lÃ²ng táº£i MetaMask!');
      }

      await window.ethereum.request({ method: 'eth_requestAccounts' });
      const web3Provider = new ethers.BrowserProvider(window.ethereum);
      setProvider(web3Provider);
      
      const web3Signer = await web3Provider.getSigner();
      setSigner(web3Signer);
      
      const userAddress = await web3Signer.getAddress();
      setAccount(userAddress);

      // Initialize contracts
      const artToken = new ethers.Contract(ART_TOKEN_ADDRESS, ART_TOKEN_ABI, web3Signer);
      const auction = new ethers.Contract(AUCTION_ADDRESS, AUCTION_ABI, web3Signer);
      
      setArtTokenContract(artToken);
      setAuctionContract(auction);

      showMessage('Káº¿t ná»‘i vÃ­ thÃ nh cÃ´ng!', 'success');
    } catch (error) {
      console.error('Lá»—i káº¿t ná»‘i vÃ­:', error);
      showMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchNFTs = async () => {
    try {
      const response = await fetch(`${API_BASE}/nfts`);
      if (!response.ok) throw new Error('KhÃ´ng thá»ƒ láº¥y NFTs');
      const data = await response.json();
      setNfts(data);
    } catch (error) {
      console.error('Lá»—i táº£i NFTs:', error);
      showMessage('KhÃ´ng thá»ƒ táº£i NFTs');
    }
  };

  const fetchActiveAuctions = async () => {
    try {
      const response = await fetch(`${API_BASE}/active-auctions`);
      if (!response.ok) throw new Error('KhÃ´ng thá»ƒ láº¥y Ä‘áº¥u giÃ¡');
      const data = await response.json();
      setAuctions(data);
    } catch (error) {
      console.error('Lá»—i táº£i Ä‘áº¥u giÃ¡:', error);
      showMessage('KhÃ´ng thá»ƒ táº£i Ä‘áº¥u giÃ¡');
    }
  };

  const createNFT = async (e) => {
    e.preventDefault();
    if (!nftForm.name || !nftForm.image) {
      showMessage('Vui lÃ²ng Ä‘iá»n táº¥t cáº£ cÃ¡c trÆ°á»ng báº¯t buá»™c');
      return;
    }

    setLoading(true);
    try {
      console.log('ğŸ”„ Báº¯t Ä‘áº§u quÃ¡ trÃ¬nh táº¡o NFT...');
      console.log('ğŸ“ NFT Data:', nftForm);

      // Upload artwork to backend
      const formData = new FormData();
      formData.append('name', nftForm.name);
      formData.append('description', nftForm.description || '');
      formData.append('artist', 'Digital Artist');
      formData.append('artistAddress', account);
      formData.append('image', nftForm.image);

      console.log('ğŸ“¤ Äang gá»­i request upload Ä‘áº¿n backend...');
      
      const uploadResponse = await fetch(`${API_BASE}/upload-artwork`, {
        method: 'POST',
        body: formData
        // KhÃ´ng Ä‘áº·t headers Content-Type, browser sáº½ tá»± Ä‘á»™ng set vá»›i FormData
      });

      console.log('ğŸ“¨ Response status:', uploadResponse.status);
      console.log('ğŸ“¨ Response headers:', uploadResponse.headers);

      // Kiá»ƒm tra náº¿u response lÃ  HTML (lá»—i)
      const contentType = uploadResponse.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        const textResponse = await uploadResponse.text();
        console.error('âŒ Server tráº£ vá» HTML thay vÃ¬ JSON:', textResponse.substring(0, 200));
        
        // Kiá»ƒm tra náº¿u lÃ  trang lá»—i
        if (textResponse.includes('<!DOCTYPE') || textResponse.includes('<html')) {
          throw new Error('Backend server tráº£ vá» trang lá»—i. Kiá»ƒm tra server cÃ³ Ä‘ang cháº¡y khÃ´ng?');
        } else {
          throw new Error(`Server tráº£ vá» unexpected response: ${textResponse.substring(0, 100)}`);
        }
      }

      const uploadData = await uploadResponse.json();
      console.log('ğŸ“Š Upload response data:', uploadData);
      
      if (!uploadResponse.ok) {
        throw new Error(uploadData.error || `Táº£i lÃªn tháº¥t báº¡i vá»›i status: ${uploadResponse.status}`);
      }

      if (!uploadData.success) {
        throw new Error(uploadData.error || 'Upload khÃ´ng thÃ nh cÃ´ng');
      }

      console.log('âœ… Upload thÃ nh cÃ´ng, Ä‘ang mint NFT trÃªn blockchain...');

      // Mint NFT on blockchain
      if (!artTokenContract) {
        throw new Error('Contract chÆ°a Ä‘Æ°á»£c khá»Ÿi táº¡o');
      }

      const tx = await artTokenContract.safeMint(account);
      console.log('â›“ï¸ Transaction sent:', tx.hash);
      
      const receipt = await tx.wait();
      console.log('âœ… Transaction confirmed:', receipt);

      // Get token ID from event
      const transferEvent = receipt.logs.find(
        log => log.fragment && log.fragment.name === 'Transfer'
      );
      
      let tokenId = '0';
      if (transferEvent) {
        tokenId = transferEvent.args[2].toString();
      } else {
        // Fallback: tÃ¬m event Transfer theo interface
        const transferInterface = new ethers.Interface([
          'event Transfer(address indexed from, address indexed to, uint256 indexed tokenId)'
        ]);
        
        for (const log of receipt.logs) {
          try {
            const parsedLog = transferInterface.parseLog(log);
            if (parsedLog && parsedLog.name === 'Transfer') {
              tokenId = parsedLog.args[2].toString();
              break;
            }
          } catch (e) {
            // Bá» qua log khÃ´ng pháº£i Transfer event
          }
        }
      }

      console.log('ğŸ¯ Token ID minted:', tokenId);

      // Save metadata to backend
      console.log('ğŸ’¾ Äang lÆ°u metadata...');
      const saveResponse = await fetch(`${API_BASE}/nft-metadata`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          tokenId,
          ...uploadData.metadata,
          imageIpfsHash: uploadData.imageIpfsHash,
          metadataIpfsHash: uploadData.metadataIpfsHash
        })
      });

      if (!saveResponse.ok) {
        const errorText = await saveResponse.text();
        throw new Error(`LÆ°u siÃªu dá»¯ liá»‡u tháº¥t báº¡i: ${errorText}`);
      }

      const saveData = await saveResponse.json();
      console.log('âœ… Metadata saved:', saveData);

      showMessage('Táº¡o NFT thÃ nh cÃ´ng!', 'success');
      setNftForm({ name: '', description: '', image: null });
      
      // Reset file input
      const fileInput = document.querySelector('input[type="file"]');
      if (fileInput) fileInput.value = '';
      
      fetchNFTs();
      
    } catch (error) {
      console.error('âŒ Lá»—i táº¡o NFT:', error);
      
      let errorMessage = error.message;
      if (error.message.includes('fetch') || error.message.includes('Network')) {
        errorMessage = 'KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n server backend. Kiá»ƒm tra xem server cÃ³ Ä‘ang cháº¡y trÃªn port 5000 khÃ´ng?';
      } else if (error.message.includes('IPFS')) {
        errorMessage = 'Lá»—i káº¿t ná»‘i IPFS. Cháº¯c cháº¯n ráº±ng IPFS daemon Ä‘ang cháº¡y: ipfs daemon';
      }
      
      showMessage(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const createAuction = async (e) => {
    e.preventDefault();
    if (!auctionForm.tokenId || !auctionForm.startPrice || !auctionForm.duration) {
      showMessage('Vui lÃ²ng Ä‘iá»n táº¥t cáº£ cÃ¡c trÆ°á»ng báº¯t buá»™c');
      return;
    }

    setLoading(true);
    try {
      const tx = await auctionContract.createAuction(
        auctionForm.tokenId,
        ethers.parseEther(auctionForm.startPrice),
        auctionForm.duration
      );
      await tx.wait();
      
      showMessage('Táº¡o Ä‘áº¥u giÃ¡ thÃ nh cÃ´ng!', 'success');
      setAuctionForm({ tokenId: '', startPrice: '', duration: '' });
      fetchActiveAuctions();
    } catch (error) {
      console.error('Lá»—i táº¡o Ä‘áº¥u giÃ¡:', error);
      showMessage(error.message);
