      }

      const tx = await artTokenContract.safeMint(account);
      console.log('â›“ï¸ Transaction sent:', tx.hash);
      showMessage('Äang xÃ¡c nháº­n giao dá»‹ch trÃªn blockchain...', 'success');
      
      const receipt = await tx.wait();
      console.log('âœ… Transaction confirmed:', receipt);

      // Get token ID from event
      let tokenId = '1'; // Fallback
      try {
        const transferEvent = receipt.logs.find(
          log => log.fragment && log.fragment.name === 'Transfer'
        );
        
        if (transferEvent) {
          tokenId = transferEvent.args[2].toString();
        } else {
          // Fallback: láº¥y tá»« currentTokenId
          tokenId = await artTokenContract.currentTokenId();
        }
      } catch (e) {
        console.warn('âš ï¸ KhÃ´ng thá»ƒ láº¥y tokenId tá»« event, sá»­ dá»¥ng fallback');
      }

      console.log('ðŸŽ¯ Token ID:', tokenId);

      // Save metadata to backend
      console.log('ðŸ’¾ Äang lÆ°u metadata...');
      const saveResponse = await fetch(`${API_BASE}/nft-metadata`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
